.block__media__heading {
  margin-top: 0;
}

.block__media .grid {
  justify-content: center;
}

.block__media .media {
  justify-self: center;
}

.block__media__text {
  font-size: 1.6rem;
  font-weight: 700;
  margin-bottom: 3rem;
}

.block__media .grid {
  gap: 3rem;
}

@media screen and (min-width: 768px) {
  .block__media__grid {
    grid-template-columns: repeat(3, 1fr);
  }
}

/* plan blocks */

.block__plan {
  padding-top: 4rem;
}

.block__plan .card {
  max-width: 500px;
  margin: 0 auto;
}

.block__plan .grid {
  gap: 4rem;
}

.block__header__text {
  margin-bottom: 6rem;
}

/* domain blocks */

.block__domain {
  max-width: 700px;
  padding-top: 4rem;
}

.block__domain__heading {
  text-align: center;
}

/* Showcase block */

.block__showcase .input-group {
  margin: 0 auto;
}

.block__showcase .list__social {
  margin-bottom: 6rem;
}

.block__showcase__text {
  margin-bottom: 0;
}

.block__showcase__content {
  margin: 0 auto;
}

.block__showcase .list__social {
  display: flex;
  justify-content: center;
}

.block__showcase .grid {
  gap: 4rem;
}

.block__showcase .block__header {
  margin-bottom: 6rem;
}

.block__showcase__heading {
  margin-top: 0;
}

@media screen and (min-width: 768px) {
  .block__showcase .input-group {
    order: -1;
  }

  .block__showcase .list__social {
    order: -1;
  }
}

/* Footers */

.footer__heading {
  margin: 0 auto;
}

.footer .grid {
  gap: 5rem;
}

.footer__brand {
  margin: 0 auto;
}

.footer__list {
  text-align: center;
}

.footer__lists {
  margin: 0 auto;
}

.footer .list__icon {
  margin-bottom: 5rem;
}

@media screen and (min-width: 768px) {
  .footer .collapsible__chevron {
    display: none;
  }

  .footer .collapsible__content {
    opacity: 1;
    max-height: 100%;
  }

  .collapsible__heading,
  .footer__brand {
    margin: 0;
  }

  .footer .grid--1x3 {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
  }

  .footer__list {
    text-align: left;
  }
}

/* Hero */

.hero__image > img {
  width: 100%;
}

.hero__content {
  text-align: center;
}

.hero__heading {
  margin-top: 0;
}

.hero .grid {
  gap: 3rem 6rem;
}

.hero .btn {
  padding: 1.5rem 4rem;
}

.hero__text {
  font-size: 1.6rem;
  font-weight: 700;
}

@media screen and (min-width: 768px) {
  .hero .hero__content {
    text-align: left;
    padding-top: 2rem;
  }

  .hero__content {
    max-width: 45rem;
  }

  .hero__image {
    justify-self: end;
    order: -1;
  }

  .hero__image > img {
    width: auto;
    max-width: 700px;
  }

  .hero .grid {
    grid-template-columns: 50% 50%;
  }
}

/* Intro */

.intro {
  padding-top: 4rem;
}

.intro__content {
  text-align: center;
}

.intro__image {
  display: flex;
}

.intro__image > img {
  width: 100%;
}

.intro .grid {
  gap: 4rem;
}

.intro__text {
  font-size: 1.6rem;
  font-weight: 700;
}

@media screen and (min-width: 768px) {
  .intro__content {
    text-align: left;
  }
}
